mapper.select:
SELECT id,user_name AS userName,login_name AS loginName,menu_name AS menuName,module_name AS moduleName,operation,create_date AS createDate
FROM sys_operation_log where 1=1

    and login_name like '%${loginName}%'


    and create_date >= #{startTime}


    and create_date < #{endTime}

order by create_date desc
LIMIT #{listStart}, #{pageSize}

SELECT count(*)
FROM sys_operation_log where 1=1

    and login_name like '%${loginName}%'


    and create_date >= #{startTime}


    and create_date < #{endTime}
